ECT*		ClipObject; 		/* Stores the owner of the pairs */CONTEXT*	ClipContext;		/* Original (unmodified) context of the source object */int			ClipMode;			/* Clipping mode (CLIP_CUT, CLIP_COPY, CLIP_REMOVE) *//* Modification menu flag fields */int 		whoToModify = THIS_ONLY; /* Editing only one object or the whole clone family? *//* The menus */MenuHandle	appleMenu;MenuHandle 	fileMenu;MenuHandle	windowMenu;/* Shell-specific menus */MenuHandle	edit1Menu;MenuHandle	tasksMenu;MenuHandle	multitaskMenu;MenuHandle	debugMenu;/* Browser-specific menus */MenuHandle	edit2Menu;MenuHandle	viewMenu;MenuHandle	toolsMenu;